package simplenotepad.models;

import java.awt.GraphicsEnvironment;


/**
 *
 * @author Adrián Barrio Andrés
 * @email statickidz@gmail.com
 * @web https://statickidz.com
 */
public class EditorModel {
    
    public EditorModel() {
        //silence is golden
    }
    
    public int[] getFontSizes() {
        int[] fontSizes = {6,8,10,12,14,16,20,24,32,36,48,72};
        return fontSizes;
    }
    
    public String[] getFontsFamilies() {
        GraphicsEnvironment e = GraphicsEnvironment.getLocalGraphicsEnvironment();
        String[] fonts = e.getAvailableFontFamilyNames();
        return fonts;
    }

    public String getTestData() {
        return "<body style='font-family:Segoe UI'>"
                + "Lorem ipsum dolor sit amet, consectetuer adipiscing elit."
                + "Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque "
                + "penatibus et magnis dis parturient montes,"
                + "nascetur ridiculus mus. Donec quam felis, ultricies nec, pellentesque eu, pretium quis, sem."
                + "Nulla consequat massa quis enim.<br><br>"
                + "Donec pede justo, fringilla vel, aliquet nec, vulputate eget, arcu."
                + "In enim justo, rhoncus ut, imperdiet a, venenatis vitae,"
                + "justo. Nullam dictum felis eu pede mollis pretium. Integer tincidunt. "
                + "Cras dapibus. Vivamus elementum semper nisi.<br><br>"
                + "<img src=\"data:image/jpg;base64,\"/>\n"
                + "</body>";
    }
    
}
